    double toRad = Math.PI / 180.0;

    QiList QiString  names 
        = QiList.Create(new string[] {  HeadYaw ,  HeadPitch  });

    QiList QiList QiDouble  angleLists
        = QiList.Create QiList QiDouble (new QiList QiDouble []
    {
