    objBuilder.AdvertiseMethod(
test::v()
, (sig, arg) =

    {
        Console.WriteLine(
test was called
);
        return QiValue.Void;
    });
    objBuilder.AdvertiseMethod(
twice::i(i)
, (sig, arg) =

    {
        Console.WriteLine(
twice was called
);
        int i = (int)arg[0];
        return new QiInt32(i * 2).QiValue;
    });

    var obj = objBuilder.BuildObject();
    obj[
test
].Call();

    int res = (int)obj[
twice
].Call(21);
    Console.WriteLine($
Res = {res}
);
}





